import React from "react";
import VideoPlayer from "react-video-js-player";
import "./Video.css";
import mondaySdk from "monday-sdk-js";
const monday = mondaySdk();

const VideoJS = (itemId) => {
  const itemid = parseInt(itemId.itemId);
  const [videoUrl, setVideoUrl] = React.useState("");
  const [updatesArr, setupdatesArr] = React.useState([])
  // let updatesArr = [];
  let query =
    "query { items (ids: [" + itemid + "]) { name, updates{assets{url}} }}";

  React.useEffect(() => {
    monday
      .api(query)
      .then((res) => {
        console.log(res.data);
        res.data.items[0].updates.forEach((update) => {
          if (update.assets.length > 0) {
            update.assets.forEach((item) => {
              // setupdatesArr([updatesArr.push(item.url)])
              setupdatesArr([...updatesArr,item.url]);

            });
          }
        });
        // setVideoUrl(res.data.items[0].updates[0].assets[0].url)

      })
      .then((res) => console.log(res));
  }, []);

  if (updatesArr.length > 0 ) {
    return (
      <div>
        {updatesArr.map((item, index) => {
          return (
            <VideoPlayer
              key={index}
              src={item}
              playbackRates={[0.5, 2, 3, 4]}
            />
          );
        })}
      </div>
    );
  } else {
    return <div></div>;
  }
};

export default VideoJS;
