import React from "react";
import videojs from "video.js";
import "video.js/dist/video-js.css";
import "./Video.css";
import mondaySdk from "monday-sdk-js";
import AnnotationComments from "@contently/videojs-annotation-comments";
import "@contently/videojs-annotation-comments/build/css/annotations.css";
videojs.registerPlugin("annotationComments", AnnotationComments(videojs));
const monday = mondaySdk();

const VideoJS2 = ({ src, onReady, index, itemid }) => {



const [getComments, setGetComments] = React.useState([])
console.log(itemid.toString()+'k'+index.toString());

  const videoJsOptions = {
    autoplay: false,
    controls: true,
    responsive: true,
    fluid: false,
    loop: true,
    sources: [
      {
        src: src,
        // type: ‘video/mp4’
      },
    ],
  };
  const videoRef = React.useRef(null);
  const playerRef = React.useRef(null);
  React.useEffect(() => {
    // make sure Video.js player is only initialized once
    if (!playerRef.current) {
      const videoElement = videoRef.current;
      if (!videoElement) return;
      const player = (playerRef.current = videojs(
        videoElement,
        videoJsOptions,
        () => {
          console.log("readyyyy");
          onReady && onReady(player);
          var plugin = player.annotationComments(pluginOptions);
          plugin.onReady(() => {});
          plugin.registerListener("onStateChanged", (event) => {
            // event.detail = annotation state data
            monday.storage.instance
              .setItem(itemid.toString()+'k'+index.toString() , JSON.stringify(event.detail))
              .then((res) => {
                console.log(res);
              });
             monday.storage.instance.getItem(itemid.toString()+'k'+index.toString()).then((res) => {
              console.log(res);
            });
          });
        }
      ));
    } else {
      // you can update player here [update player through props]
      // const player = playerRef.current;
      // player.autoplay(options.autoplay);
      // player.src(options.sources);
    }
  }, [videoJsOptions]);
  // Dispose the Video.js player when the functional component unmounts
  React.useEffect(() => {
    return () => {
      if (playerRef.current) {
        playerRef.current.dispose();
        playerRef.current = null;
      }
    };
  }, []);
  return (
    <div data-vjs-player>
      <video
        ref={videoRef}
        className="video-js vjs-big-play-centered"
        controls
        width="640"
        height="268"
        preload="auto"
        data-setup='{ "playbackRates": [0.5, 1, 1.5, 2, 4] }'
      />
    </div>
  );
};
export default VideoJS2;
