import React from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
//Explore more Monday React Components here: https://style.monday.com/
import VideoJS from "./components/video-js/VideoJS";
const monday = mondaySdk();
class App extends React.Component {
  constructor(props) {
    super(props);

    // Default state
    this.state = {
      settings: {},
      name: "",
      context: {},
      events: {},
      taskColorLabel: '',
      workingIndex: -1,
      users: [],
      bugs: [],
      loading: false,
      itemId:0
    };
  }

  componentDidMount () {
    // monday.listen("settings", this.getSettings);
    // monday.listen("context", this.getContext);
    // monday.listen("events", this.getEvents);
    monday.listen("context", async (res) => {
      this.setState({context: res.data});
      console.log(res.data);
     await monday.api(`query ($boardIds: [Int]) { boards (ids:$boardIds) { name items(limit:1) { name id } } }`,
        { variables: {boardIds: this.state.context.boardIds} }
      )
      .then(res => {
        this.setState({itemId: res.data.boards[0].items[0].id});
      });
    })

  }

  render() {
    console.log(this.state);
    return (
      <div className="App">
        <VideoJS itemId={this.state.itemId}/>
      </div>
    );
  }
}

export default App;
